CREATE OR ALTER PROCEDURE [dbo].[DisableTriggers]@dryrun bit = 0ASDECLARE @sql nvarchar(max)SET @sql = (		select 			CONCAT('DISABLE TRIGGER ', quotename(OBJECT_SCHEMA_NAME(object_id)), '.', quotename(OBJECT_NAME(object_id))			, ' ON ', quotename(OBJECT_SCHEMA_NAME(parent_id)), '.', quotename(OBJECT_NAME(parent_id)), ';', char(10))		from 			sys.triggers		FOR XML PATH(''), TYPE 	).value('.', 'varchar(max)')if @dryrun = 1	print @sqlelse	exec sp_executesql @sql;